import GEOparse as GEO
import os

# Path to the downloaded file
soft_file = os.path.join("data", "GSE245274_family.soft.gz")

# Check if the file exists
if not os.path.exists(soft_file):
    print(f"File not found: {soft_file}")
else:
    # Load the SOFT file
    gse = GEO.parse_GSE(filepath=soft_file)

    # Identify colon samples (SPF and GF)
    colon_samples = {}
    for gsm_name, gsm in gse.gsms.items():
        # Check characteristics_ch1 for sample type
        if 'characteristics_ch1' in gsm.metadata:
            characteristics = gsm.metadata['characteristics_ch1']
            is_colon = any("colon" in s.lower() for s in characteristics)
            is_spf = any("spf" in s.lower() for s in characteristics)
            is_gf = any("gf" in s.lower() for s in characteristics)

            if is_colon and (is_spf or is_gf):
                colon_samples[gsm_name] = gsm.metadata

    # Print the identified samples and their metadata
    print(f"Found {len(colon_samples)} colon samples (SPF and GF):")
    for gsm_name, metadata in colon_samples.items():
        print(f"\nSample: {gsm_name}")
        for key, value in metadata.items():
            if key == 'characteristics_ch1':
                print(f"  {key}:")
                for item in value:
                    print(f"    - {item}")
            else:
                print(f"  {key}: {value}")

